    public static long minimalKSum(int[] nums, int k) {

        int kTemp = k;
        long ans = (long) k * ((long) (k + 1));
        ans /= 2;
        for(int i = 0; i < nums.length; i++) {
            int temp = nums[i];
            if(nums[i] >=1 && nums[i] <= k) {
                ans -= nums[i];
                kTemp++;
                ans += kTemp;
            }
        }

        return ans;

    }
